import React, { Component } from "react";
import TodoBox from "../components/TodoBox";

export default class Todo extends Component {
  constructor(props) {
    super(props);
    this.state = {
        name:'',
        age:'',
        prof:'',
      Employess: [
        { id: "1", name: "sravya", age: "22", prof: "software" },
        { id: "2", name: "aakash", age: "32", prof: "Doctor" },
        { id: "3", name: "hema", age: "62", prof: "teacher" },
        { id: "4", name: "venu", age: "42", prof: "Politician" },
      ],
    };
  }

  handleRemoveTodo=(id)=>{
      const items = this.state.Employess.filter(item => item.id !== id);
      this.setState({ Employess: items });
  }

  handleChange=(e)=>{
      console.log(e.target.value);
  }


  render() {
    return (
      <React.Fragment>
          <form onSubmit={this.handleSubmit}>
              <input onChange={this.handleChange} type="text" name="name" value={this.state.name} required placeholder="Enter your name"/>
              <input onChange={this.handleChange} type="age" name="age" value={this.state.age} required placeholder="Enter your age"/>
              <input onChange={this.handleChange} type="prof" name="prof" value={this.state.prof} required placeholder="Enter your prof"/>
<button>Submit</button>
          </form>
        {this.state.Employess.map((emp) => {
          return (
            <TodoBox
              key={emp.id}
              name={emp.name}
              age={emp.age}
              prof={emp.prof}
              removeClicked={()=>this.handleRemoveTodo(emp.id)}
            />
          );
        })}
      </React.Fragment>
    );
  }
}
